import React, { useState, useEffect } from 'react';
import './App.css';
import { RiDeleteBin5Fill } from "react-icons/ri";
import { BsClipboardCheckFill } from "react-icons/bs";

function App() {
  const [isCompleteScreen,setIsCompleteScreen] = useState(false);
  const [allTodos, setAllTodos] = useState ([]);
  const [newTodoTitle, setNewTodoTitle] = useState ('');
  const [newDescription, setNewDescription] = useState ('');

  const handleAddNewToDo = () => {
    let newToDoObj = {
      title: newTodoTitle,
      description: newDescription,
    }

    let updatedTodoArr = [...allTodos];
    updatedTodoArr.push(newToDoObj);
    setAllTodos(updatedTodoArr);
    localStorage.setItem ('todolist', JSON.stringify (updatedTodoArr));
  }

  const handleToDoDelete = index => {
    let reducedTodos = [...allTodos];
    reducedTodos.splice (index,1);
    localStorage.setItem ('todolist', JSON.stringify (reducedTodos));
    setAllTodos (reducedTodos);
  };

  useEffect(()=>{
    let savedTodos = JSON.parse (localStorage.getItem ('todolist'));
    if (savedTodos) {
      setAllTodos (savedTodos);
    }
  },[])

  return (
    <div className="App">
      <h1>My ToDos</h1>
      <div className='todo-wrapper'>
        <div className='todo-input'>
          <div className='todo-input-item'>
            <label>Title</label>
            <input type='text' value={newTodoTitle} onChange={(e)=>setNewTodoTitle(e.target.value)} placeholder="What's the task title?" />
          </div>
          <div className='todo-input-item'>
            <label>Description</label>
            <input type='text' value={newDescription} onChange={(e)=>setNewDescription(e.target.value)} placeholder="What's the task description?" />
          </div>
          <div className='todo-input-item'>
            <button type='button' onClick={handleAddNewToDo} className='primaryBtn'>Add</button>
          </div>
        </div>
        <div className='btn-area'>
          <button className={`secondaryBtn ${isCompleteScreen===false && 'active'}`} onClick={()=>setIsCompleteScreen(false)}>Todo</button>
          <button className={`secondaryBtn ${isCompleteScreen===true && 'active'}`}onClick={()=>setIsCompleteScreen(true)}>Completed</button>
        </div>
        <div className='todo-list'>
          {allTodos.map((item,index)=>{
            return(
              <div className='todo-list-item' key={index}>
            <div>
              <h3>{item.title}</h3>
              <p>{item.description}</p>
            </div>
            <div>
              <RiDeleteBin5Fill title="Delete?" className='icon' onClick={()=>handleToDoDelete (index)} />
              <BsClipboardCheckFill title="Completed?" className='check-icon' />
            </div>
          </div>
            )
          })}
        </div>  
      </div>
    </div>
  );
}

export default App;
